<script setup>
import AppLayout from '@/Layouts/AppLayout.vue';
import { ref, onMounted, onUnmounted, computed } from 'vue';
import { Swiper, SwiperSlide } from 'swiper/vue';
import 'swiper/swiper-bundle.css';
import { useForm } from 'laravel-precognition-vue-inertia';
import { useToast } from "primevue/usetoast";
import Toast from 'primevue/toast';
import Textarea from 'primevue/textarea';
import Button from 'primevue/button';
import Modal from '@/Components/Modal.vue';

const props = defineProps({
    immobile: Object,
});

//  Swiper Container 
const windowWidth = ref(window.innerWidth);

const slidesPerView = computed(() => {
    return windowWidth.value > 768 ? 5 : 2;
});

function onResize() {
    windowWidth.value = window.innerWidth;
}

onMounted(() => {
    window.addEventListener('resize', onResize);
});
onUnmounted(() => {
    window.removeEventListener('resize', onResize);
});

// Avaliações e Comentários
const toast = useToast();

const form = useForm('post', route('rating', props.immobile.id), {
    comment: '',
});

const submitComment = () => form.submit({
    preserveScroll: true,
    onSuccess: () => {
        form.reset()
        toast.add(
            { severity: 'success', summary: 'Sucesso', detail: 'Imóvel cadastrado com sucesso', life: 3000 }
        );
    },
    onError: () => {
        toast.add(
            { severity: 'error', summary: 'Erro', detail: 'Erro ao cadastrar imóvel', life: 3000 }
        );
    }
});


// modal deletar comentario
const deleteComment = ref(false);
const formDelete = ref();

const openModalDelete = (id) => {
    console.log(id);
    deleteComment.value = true;
    formDelete.value = useForm('delete',`/delete-comentario/${id}`, {
        id: id,
    });
};

const closeModalDelete = () => {
    deleteComment.value = false;
    formDelete.value = null;
};

const submitDelete = () => {
    formDelete.value.submit({
        preserveScroll: true,
        onSuccess: () => {
            closeModalDelete();
            toast.add(
                { severity: 'success', summary: 'Sucesso', detail: 'Comentário deletado com sucesso', life: 3000 }
            );
        },
        onError: () => {
            closeModalDelete();
            toast.add(
                { severity: 'error', summary: 'Erro', detail: 'Erro ao deletar comentário', life: 3000 }
            );
        }
    });
};

</script>

