            <div class="col-span-1 lg:col-span-2 border-slate-100 border-4 bg-white rounded-2xl p-4">
                <h2 class="text-lg font-bold">Comentários e Avaliações:</h2>
                <form @submit.prevent="submitComment">
                    <div>
                        <Textarea v-model="form.comment" rows="5" cols="30" autoResize />
                        <Button label="Salvar" class="m-4" type="submit" />
                    </div>
                </form>
                <div v-for="evaluation in immobile.evaluations" :key="evaluation.id" class="mt-4">
                    <div class="flex justify-between items-center">
                        <!-- <img :src="comment.user.profile_photo_url" alt="Foto do usuário" class="h-10 w-10 rounded-full"> -->
                        <div class="ml-2">
                            <div class="font-semibold">{{ evaluation.user.name }}</div>
                            <div class="text-sm">{{ evaluation.user.email }}</div>
                        </div>
                        <button @click="openModalDelete(evaluation.id)">
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5"
                                stroke="currentColor" class="w-6 h-6 hover:scale-125 ease-in-out hover:stroke-red-500">
                                <path stroke-linecap="round" stroke-linejoin="round"
                                    d="M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0" />
                            </svg>
                        </button>
                    </div>

                    <div class="mt-2">
                        <p>{{ evaluation.comment }}</p>
                        <!-- <span class="text-sm">Nota: {{ comment.rating }}</span> -->
                    </div>

                </div>
            </div>
        </div>
    </AppLayout>

    <!-- modal para exclusão de comentario -->
    <Modal :show="deleteComment" @cloze="deleteComment = false">
        <form @submit.prevent="submitDelete">
            <div class="p-4">
                <h2 class="text-lg font-bold">Deseja realmente excluir este comentário?</h2>
                <div class="flex justify-between mt-4">
                    <Button label="Cancelar" @click="deleteComment = false" />
                    <Button label="Excluir" @click="submitDelete" />
                </div>
            </div>
        </form>
    </Modal>
